<template>
<div :style="{height:height,width:width}"></div>
</template>
<script>
import echarts from 'echarts'
const BULL =
  'image://data:image/png;base64,'

export default {
  props: {
    width: {
      type: String,
      width: '100%'
    },
    height: {
      type: String,
      default: '100%'
    },
    chartName: {
      type: String
    },
    xAxisData: {
      type: Array
    },
    barData: {
      type: Array
    },
    barColor: {
      type: String,
      default: '#f79300'
    },
    fontColor: {
      type: String,
      default: '#fff'
    },
    barHoverColor: {
      type: String,
      default: null
    }
  },
  data() {
    return {
      chart: null,
      spirit: '',
      options: {
        tooltip: {},
        toolbox: {
          show: true,
          right: 20,
          feature: {
            dataView: {
              show: true,
              readOnly: true,
              optionToContent(opt) {
                const table =
                  `
                  <div style="width:100%;height:100%;border-radius: 2px;padding: 0 25px;padding-top: 2px;">
                    <div style="width: 100%;height:100%;
                    box-shadow: 0px 1px 5px 0px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 3px 1px -2px rgba(0, 0, 0, 0.12);
                    display: flex;flex-direction: column;
                    ">
                      <h3 style="padding-left: 24px;color: rgba(0, 0, 0, 0.87);font-size: 21px;font-weight: 500;
                      border-bottom: 1px solid #ebebeb;flex: 1;display: flex;align-items: center;">${opt.series[0].name}</h3>
                      <ul style="display: flex;color: rgba(0, 0, 0, 0.54);background: #fff;border-bottom: 1px solid #ebebeb;flex: 1;"
                      onmouseover="this.style.backgroundColor='#eeeeee'" onmouseout="this.style.backgroundColor='#fff'">
                        <li style="flex: 1;display: flex;justify-content: center;align-items: center;">本周</li>
                        <li style="flex: 1;display: flex;justify-content: center;align-items: center;">${opt.series[0].data[0]}</li>
                      </ul>
                      <ul style="display: flex;color: rgba(0, 0, 0, 0.54);background: #fff;border-bottom: 1px solid #ebebeb;flex: 1;"
                      onmouseover="this.style.backgroundColor='#eeeeee'" onmouseout="this.style.backgroundColor='#fff'">
                        <li style="flex: 1;display: flex;justify-content: center;align-items: center;">前一周</li>
                        <li style="flex: 1;display: flex;justify-content: center;align-items: center;">${opt.series[0].data[1]}</li>
                      </ul>
                      <ul style="display: flex;color: rgba(0, 0, 0, 0.54);background: #fff;border-bottom: 1px solid #ebebeb;flex: 1;"
                      onmouseover="this.style.backgroundColor='#eeeeee'" onmouseout="this.style.backgroundColor='#fff'">
                        <li style="flex: 1;display: flex;justify-content: center;align-items: center;">前二周</li>
                        <li style="flex: 1;display: flex;justify-content: center;align-items: center;">${opt.series[0].data[2]}</li>
                      </ul>
                    </div>
                  </div>
                `
                return table
              }
            },
            saveAsImage: { // 保存图片
              show: true,
              name: this.chartName
            }
          }
        },
        xAxis: {
          // max: 100,
          splitLine: {
            show: false
          },
          offset: 10,
          axisLine: {
            lineStyle: {
              color: '#999'
            }
          },
          minInterval: 1
          // axisLabel: {
          //   margin: 10
          // }
        },
        yAxis: {
          data: ['前二周', '前一周', '本周'],
          inverse: true,
          axisTick: {
            show: false
          },
          axisLine: {
            show: false
          },
          axisLabel: {
            margin: 10,
            textStyle: {
              color: '#999',
              fontSize: 12
            }
          }
        },
        grid: {
          top: 'center',
          height: '70%',
          left: 50
        },
        series: [
          {
          // current data
            name: this.chartName,
            type: 'pictorialBar',
            symbol: BULL,
            symbolRepeat: 'true',
            symbolMargin: '5%',
            symbolClip: true,
            symbolSize: 25,
            data: [],
            z: 10
          },
          {
          // full data
            type: 'pictorialBar',
            itemStyle: {
              normal: {
                opacity: 0.2
              }
            },
            label: {
              normal: {
                show: true,
                position: 'right',
                offset: [0, 4],
                textStyle: {
                  color: 'green',
                  fontSize: 12
                }
              }
            },
            animationDuration: 0,
            symbolRepeat: 'true',
            symbolMargin: '5%',
            symbol: BULL,
            symbolSize: 25,
            data: [],
            z: 5
          }]
      }
    }
  },
  beforeDestroy() {
    this.chart.dispose()
    this.chart = null
    if (!this.chart) {
      window.removeEventListener('resize')
    }
  },
  mounted() {},
  methods: {
    initChart() {
      const wrapper = this.$el
      this.chart = echarts.init(wrapper)
      this.chart.setOption(this.options)
    }
  },
  watch: {
    barData: {
      handler(newVal, oldVal) {
        const data = [...newVal].reverse()
        this.options.series[0].data = data
        this.options.series[1].data = data
        this.initChart()
        if (this.chart) {
          window.addEventListener('resize', this.chart.resize)
        }
      }
    }
  }
}
</script>
